// --------  ПЕРЕМЕННЫЕ--------------//
const arrOfMonths = [
  'Январь',
  'Февраль',
  'Март',
  'Апрель',
  'Май',
  'Июнь',
  'Июль',
  'Август',
  'Сентябрь',
  'Октябрь',
  'Ноябрь',
  'Декабрь',
];
const arrOfWeekDays = ['Пн', 'Вт', 'Ср', 'Чт', 'Пт', 'Сб', 'Вс'];
const arrOfColorMonth = ['#F5FAFF'] * 12;
let currentYear;
let currentNumberOfMonth;
let r = [];
let calendarContentNode = document.getElementById('calendar_content');
let weekDaysContentNode = document.getElementById('calendar_weekdays');
let headerContentNode = document.getElementById('calendar_header');
let filteredDaysArr = [];

// ------------ФУНКЦИИ----------//

// функция устанавливает в переменную t - год, а в переменную n - номер месяца
function getCurrentYearAndMonth() {
  let currentDate = new Date();
  currentYear = currentDate.getFullYear();
  currentNumberOfMonth = currentDate.getMonth() + 1;
}

// функция получает массив объектов с номером дня и названием дня недели [{day: 1, weekday: 'Вт'}, {day: 2, weekday: 'Ср'} и т.д.]
function getArrOfCurrentWeekDaysOfMonth(year, month) {
  let arrWeekdays = [];
  for (
    let indexDay = 1;
    indexDay < getDaysInMonth(year, month) + 1;
    indexDay++
  ) {
    arrWeekdays.push({
      day: indexDay,
      weekday: arrOfWeekDays[getNumberOfWeekday(year, month, indexDay)],
    });
  }

  return arrWeekdays;
}

// функция отрисовывает дни недели в календаре
function fillNameOfWeekdays(weekDaysContentNode) {
  // удаляем все дочерние узлы из
  weekDaysContentNode.innerHTML = ''; // очищаем содержимое блока названий дней недели
  // заполняем календарь названиями дней недели
  for (let weekDay = 0; weekDay < 7; weekDay++) {
    let nameOfWeek = document.createElement('div');
    nameOfWeek.innerHTML = `${arrOfWeekDays[weekDay]}`;
    nameOfWeek.classList.add('weekname');
    weekDaysContentNode.append(nameOfWeek);
  }
}

// размечаем и раскрашиваем календарь
function markupOfCalendar() {
  let colorOfMonth = arrOfColorMonth[currentNumberOfMonth - 1];
  let todayElement = calendarContentNode.querySelector('.today');

  // раскрашиваем квадраты дней недели в цвета месяца за исключением выходных (они красные)
  // weekDaysContentNode.querySelectorAll('.weekname').forEach((item) => {
  //     item.textContent == 'Сб' || item.textContent == 'Вс' ? item.style.cssText = `color: red;` : item.style.cssText = `color: ${colorOfMonth};`
  // });
  weekDaysContentNode.querySelectorAll('.weekname').forEach(item => {
    if (
      item.textContent.toLocaleLowerCase() == 'сб' ||
      item.textContent.toLocaleLowerCase() == 'вс'
    ) {
      item.style.cssText = 'color: black;';
    }
  });

  // выводим месяц и год в календарь и подкрашиваем заголовок календаря в цвет месяца
  headerContentNode.querySelector('h1').textContent =
    `${arrOfMonths[currentNumberOfMonth - 1]} ${currentYear}`;
  headerContentNode.style.cssText = `background-color: ${colorOfMonth};`;
}

// функция проверяет на соответствие принимаемой даты текущей дате
function isDateEqualToday(date) {
  return getFormatDate(new Date()) == getFormatDate(date);
}

// функция принимает дату и возвращает ее в формате '2024/4/24'
function getFormatDate(date) {
  return (
    date.getFullYear() + '/' + (date.getMonth() + 1) + '/' + date.getDate()
  );
}

// возвращает количество дней в выбранном месяце
function getDaysInMonth(currentYear, currentMonth) {
  return new Date(currentYear, currentMonth, 0).getDate();
}

// возвращает номер недели, к которой относится дата
function getNumberOfWeekday(currentYear, currentMonth, currentDay) {
  return new Date(currentYear, currentMonth - 1, currentDay - 1).getDay();
}

// функция принимает и возвращает массив отфильтрованных дней
function setFilteredDaysArr(newFilteredArr) {
  if (newFilteredArr && newFilteredArr.length > 0) {
    filteredDaysArr.splice(0, filteredDaysArr.length);
    filteredDaysArr.push.apply(filteredDaysArr, newFilteredArr);
    drawSelectedDays(filteredDaysArr);
  } else {
    return;
  }
}

// функция сбрасывает выбранный день
function resetSelectedDays() {
  calendarContentNode.querySelectorAll('.selected').forEach(day => {
    day.classList.remove('selected');
  });
}

// функция подкрашивает выбранные в фильтре дни
function drawSelectedDays(filteredDaysArr) {
  if (filteredDaysArr.length !== 0) {
    document.querySelectorAll('.day').forEach(day => {
      for (let item of filteredDaysArr) {
        let date = new Date(item);
        if (
          Number(day.textContent) == Number(date.getDate()) &&
          date.getMonth() + 1 == currentNumberOfMonth
        ) {
          day.classList.add('filteredDay');
        }
      }
    });
  } else {
    return;
  }
}

function calendar(year, month, today = true) {
  fillNameOfWeekdays(weekDaysContentNode); // заполняем календарь названиями дней недели

  let arrDays = getArrOfCurrentWeekDaysOfMonth(year, month); // создаем и возвращаем массив из объектов в виде {day, weekday}, начиная с текущей даты

  calendarContentNode.innerHTML = ''; // очищаем контент календаря

  // заполняем темным цветом дни, которые относятся к прошлому месяцу
  let arrDaysPrev = getArrOfCurrentWeekDaysOfMonth(year, month - 1);
  let IndexDayStart = 0;
  let isWeekdayEqualWeekdayFromArr1 = false;
  let IndexDay1 = 0;
  while (!isWeekdayEqualWeekdayFromArr1) {
    if (arrOfWeekDays[IndexDay1] == arrDays[0].weekday) {
      isWeekdayEqualWeekdayFromArr1 = true;
    } else {
      IndexDayStart = arrDaysPrev[arrDaysPrev.length - IndexDay1 - 1].day;
      IndexDay1++;
    }
  }

  let isWeekdayEqualWeekdayFromArr = false;
  let IndexDay = 0;

  while (!isWeekdayEqualWeekdayFromArr) {
    let blankDay = document.createElement('div');
    blankDay.className = 'day blank';

    if (arrOfWeekDays[IndexDay] == arrDays[0].weekday) {
      isWeekdayEqualWeekdayFromArr = true;
    } else {
      calendarContentNode.append(blankDay);
      blankDay.textContent = IndexDayStart;
      var month1 = month - 1;
      var year1 = year;

      if (month == 1) {
        month1 = 12;
        year1 = year1 - 1;
      }
      blankDay.id = IndexDayStart + '/' + month1 + '/' + year1;

      IndexDayStart = IndexDayStart + 1;
      IndexDay++;
    }
  }
  let indexDayFuture = 1;
  // заполняем календарные дни (6 недель по 7 дней...итого 42 дня)
  for (let indexDay = 0; indexDay < 42 - IndexDay; indexDay++) {
    // создаем элементы для обычного дня и для сегодняшнего дня
    let usualDay = document.createElement('div');
    usualDay.classList.add('day');
    usualDay.id = indexDay + 1 + '/' + currentNumberOfMonth + '/' + currentYear;

    // если номер дня выходит за диапазон количества дней в месяце, то дальше дни помечаем темным цветом

    if (indexDay >= arrDays.length) {
      let blankDay = document.createElement('div');
      blankDay.classList.add('blank');
      blankDay.classList.add('day');
      blankDay.textContent = indexDayFuture;
      var month2 = month + 1;
      var year2 = year;
      if (month == 12) {
        month2 = 1;
        year2 = year2 + 1;
      }
      blankDay.id = indexDayFuture + '/' + month2 + '/' + year2;
      calendarContentNode.append(blankDay);
      indexDayFuture = indexDayFuture + 1;
    } else {
      let dayOfArrDays = arrDays[indexDay].day;
      // если дата равна текущей, то помечаем его классом сегодняшнего дня
      if (isDateEqualToday(new Date(year, month - 1, dayOfArrDays))) {
        usualDay.textContent = `${dayOfArrDays}`;
        if (today == true) {
          usualDay.classList.add('today', 'selected');
        }
        calendarContentNode.append(usualDay);
      }
      // в противном случае добавляем обычный див
      else {
        usualDay.textContent = `${dayOfArrDays}`;
        calendarContentNode.append(usualDay);
      }
      // раскрашиваем выходные дни
      if (
        arrDays[indexDay].weekday.toLocaleLowerCase() == 'сб' ||
        arrDays[indexDay].weekday.toLocaleLowerCase() == 'вс'
      ) {
        usualDay.classList.add('holiday');
      }
    }
  }
  // подкрашиваем выбранный день
  calendarContentNode.querySelectorAll('.day').forEach(day => {
    day.addEventListener('click', () => {
      resetSelectedDays();
      day.classList.add('selected');
    });
  });

  // запускаем процесс разметки календаря
  markupOfCalendar();
}

// устанавливаем значения переменных currentYear и currentNumberOfMonth
getCurrentYearAndMonth();

// запускаем календарь
calendar(currentYear, currentNumberOfMonth);

// раскрашиваем отфильтрованные дни
setFilteredDaysArr([]);

// навешиваем события на кнопки "назад" и "вперед"
headerContentNode.querySelectorAll('[class^="icon-chevron"]').forEach(item => {
  item.addEventListener('click', function () {
    let selectorMonth = function (selection) {
      currentNumberOfMonth =
        selection == 'next'
          ? currentNumberOfMonth + 1
          : currentNumberOfMonth - 1;
      if (currentNumberOfMonth < 1) {
        currentNumberOfMonth = 12;
        currentYear--;
      } else if (currentNumberOfMonth > 12) {
        currentNumberOfMonth = 1;
        currentYear++;
      }
      // после перехода на нужный месяц перезапускаем календарь
      calendar(currentYear, currentNumberOfMonth);
      drawSelectedDays(filteredDaysArr);
    };
    item.classList.contains('icon-chevron-left')
      ? selectorMonth('previous')
      : selectorMonth('next');
  });
});

function select_day(day) {
  let elem = document.getElementById(day);
  resetSelectedDays();
  elem.classList.add('selected');
}

var selectDay = function (p) {
  return select_day(p);
};

var changeCalendar = function (year, month, today) {
  currentNumberOfMonth = month;
  currentYear = year;
  return calendar(year, month, today);
};

