function hide_block(elem) {
  const common = document.getElementById('common-block');
  common.style.height = 942 + 'px';
  menu_height = 942;
  const parent = elem.parentNode;
  elem.classList.remove('block-curr');
  parent.childNodes[1].style.borderRadius = '8px 8px 8px 8px';

  parent.childNodes[3].style.height = '0px';
  var item = document.getElementsByClassName('common-subblock-curr');
  for (var i = 0; i < item.length; i++) {
    var submenu = item[i].parentNode;
    var block = submenu.childNodes[1];
    if (block.className == 'block' || block.className == 'block page-curr') {
      item[i].classList.remove('common-subblock-curr');
    }
  }

  elem.childNodes[5].className = 'down';
  for (var j = 0; j < parent.childNodes[3].childNodes.length; j++) {
    if ((parent.childNodes[3].childNodes[j].className = 'subpoint-curr')) {
      parent.childNodes[3].childNodes[j].className = 'subpoint';
    }
  }
}

function hide_blocks(p) {
  var elements = document.getElementsByClassName('block-curr');
  for (var i = 0; i < elements.length; i++) {
    if (!elements[i].classList.contains('page-curr')) {
      hide_block(elements[i]);
    }
  }
}

function block_click(elem, activate) {
  if (elem.classList.contains('single')) {
    var elements = document.getElementsByClassName('block-curr');
    var elements = document.getElementsByClassName('page-curr');
    for (var i = 0; i < elements.length; i++) {
      if (activate) {
        change_icon(elements[i], false);
        elements[i].classList.remove('page-curr');
      }
    }
    let subpoints_curr = document.querySelectorAll('.subpoint-page-curr');
    for (var i = 0; i < subpoints_curr.length; i++) {
      if (activate) {
        subpoints_curr[i].classList.remove('subpoint-page-curr');
      }
    }

    if (activate) {
      elem.classList.add('page-curr');
      change_icon(elem, true);
    }
    return;
  } else {
    if (!menu_open) {
      menu_was_closen = true;
      open_menu();
    }
  }

  const parent = elem.parentNode;
  const common = document.getElementById('common-block');

  if (elem.classList.contains('block-curr')) {
    hide_block(elem);
  } else {
    var elements = document.getElementsByClassName('block-curr');
    for (var i = 0; i < elements.length; i++) {
      hide_block(elements[i]);
    }

    parent.childNodes[1].style.borderRadius = '8px 8px 0px 0px';

    parent.childNodes[1].classList.add('block-curr');

    elem.childNodes[5].className = 'up';

    var count = 0;
    for (var j = 0; j < parent.childNodes[3].childNodes.length; j++) {
      if ((parent.childNodes[3].childNodes[j].className = 'subpoint')) {
        count = count + 1;
      }
    }
    var height = 38 * ((count - 1) / 2) + 2;
    var height_str = height + 'px';
    parent.childNodes[3].classList.add('common-subblock-curr');
    parent.childNodes[3].style.height = height_str;
    common.style.height = menu_height + height + 'px';

    for (var j = 0; j < parent.childNodes[3].childNodes.length; j++) {
      if ((parent.childNodes[3].childNodes[j].className = 'subpoint')) {
        parent.childNodes[3].childNodes[j].className = 'subpoint-curr';
      }
    }

    for (var j = 0; j < parent.childNodes[3].childNodes.length; j++) {
      if ((parent.childNodes[3].childNodes[j].className = 'subpoint')) {
        parent.childNodes[3].childNodes[j].style.display = 'block';
      }
    }
  }
}

buttons_block.forEach(elem => {
  elem.addEventListener('click', () => {
    block_click(elem, true, false);
  });
});

function subpoint_click(elem, activate, close = true) {
  var elements = document.getElementsByClassName('page-curr');
  for (var i = 0; i < elements.length; i++) {
    if (activate) {
      change_icon(elements[i], false);
      elements[i].classList.remove('page-curr');
    }
  }
  let subpoints_curr = document.querySelectorAll('.subpoint-page-curr');
  for (var i = 0; i < subpoints_curr.length; i++) {
    if (activate) {
      subpoints_curr[i].classList.remove('subpoint-page-curr');
    }
  }
  if (activate) {
    elem.classList.add('subpoint-page-curr');
  }
  var parent = elem.parentNode;
  var submenu = parent.parentNode;
  if (activate) {
    change_icon(submenu.childNodes[1], true);
    submenu.childNodes[1].classList.add('page-curr');
  }
  if (close) {
    if (menu_was_closen == true) {
      close_menu();
      menu_was_closen = false;
    }
  }
}

subpoints.forEach(elem => {
  elem.addEventListener('click', () => {
    subpoint_click(elem, true);
  });
});
